// === EMA CALCULATION (MULTIPLE PERIODS) ===
const config = {
  symbol: 'NVDA',
  emaShort: 5,
  emaLong: 20,
  confirmationPercent: 0.75,
  periods: [5, 8, 9, 13, 20, 21, 34, 50, 100, 200]
};

const barsData = $input.first().json;
const bars = barsData.bars || [];

// No bars at all
if (!bars.length) {
  return {
    json: {
      config,
      noData: true,
      message: 'No bars returned from Alpaca'
    }
  };
}

// Calculate EMA
function calculateEMA(prices, period) {
  const ema = [];
  const multiplier = 2 / (period + 1);

  let sum = 0;
  for (let i = 0; i < period; i++) sum += prices[i];
  ema[period - 1] = sum / period;

  for (let i = period; i < prices.length; i++) {
    ema[i] = (prices[i] - ema[i - 1]) * multiplier + ema[i - 1];
  }
  return ema;
}

function calculateRSI(prices, period = 14) {
  if (prices.length < period + 1) return null;
  let gains = 0;
  let losses = 0;
  for (let i = 1; i <= period; i++) {
    const diff = prices[i] - prices[i - 1];
    if (diff >= 0) gains += diff;
    else losses += Math.abs(diff);
  }
  let avgGain = gains / period;
  let avgLoss = losses / period;
  for (let i = period + 1; i < prices.length; i++) {
    const diff = prices[i] - prices[i - 1];
    const gain = diff >= 0 ? diff : 0;
    const loss = diff < 0 ? Math.abs(diff) : 0;
    avgGain = (avgGain * (period - 1) + gain) / period;
    avgLoss = (avgLoss * (period - 1) + loss) / period;
  }
  if (avgLoss === 0) return 100;
  const rs = avgGain / avgLoss;
  return 100 - (100 / (1 + rs));
}

function calculateSMA(values, period) {
  if (values.length < period) return null;
  const slice = values.slice(values.length - period);
  const sum = slice.reduce((a, b) => a + b, 0);
  return sum / period;
}

const closes = bars.map(b => parseFloat(b.c));
const volumes = bars.map(b => parseFloat(b.v));
const last = closes.length - 1;
const prev = closes.length - 2;

// Calculate all EMAs
const current = {
  close: closes[last],
  timestamp: bars[last].t
};

const previous = {
  close: closes[prev]
};

config.periods.forEach(period => {
  if (closes.length >= period) {
    const ema = calculateEMA(closes, period);
    current[`ema${period}`] = ema[last];
    previous[`ema${period}`] = ema[prev];
  } else {
    current[`ema${period}`] = null;
    previous[`ema${period}`] = null;
  }
});

current.volume = volumes[last];
current.volume_ma20 = calculateSMA(volumes, 20);
current.rsi14 = calculateRSI(closes, 14);

return {
  json: {
    config,
    current,
    previous,
    lastBar: bars[last]
  }
};

// === SIGNAL DETECTION ===
const input = $input.first().json;

// If not enough data yet, just hold and pass info through
if (input.noData || input.notEnoughBars || !input.current || !input.previous) {
  return {
    json: {
      action: 'hold',
      message: input.message || 'Waiting for enough bars to start EMA logic',
      crossover: 'none',
      state: {},
      current: input.current || {},
      currentPrice: null,
      symbol: (input.config && input.config.symbol) || 'NVDA',
      timestamp: new Date().toISOString(),
      notReady: true,
      barsAvailable: input.barsAvailable,
      requiredBars: input.requiredBars
    }
  };
}

const { current, previous, config } = input;

// Get state from static data
const state = $getWorkflowStaticData('global');
if (!state.status) {
  state.status = 'flat';
  state.crossoverPrice = null;
  state.crossoverTime = null;
  state.entryPrice = null;
}

// Detect crossover using EMA5 and EMA20
function detectCrossover(curr, prev) {
  if (!curr.ema5 || !curr.ema20 || !prev.ema5 || !prev.ema20) return 'none';
  if (prev.ema5 <= prev.ema20 && curr.ema5 > curr.ema20) return 'bullish';
  if (prev.ema5 >= prev.ema20 && curr.ema5 < curr.ema20) return 'bearish';
  return 'none';
}

const crossover = detectCrossover(current, previous);
const currentPrice = current.close;

let action = 'hold';
let message = '';

switch (state.status) {
  case 'flat':
    if (crossover === 'bullish') {
      state.status = 'waiting_confirmation';
      state.crossoverPrice = current.close;
      state.crossoverTime = current.timestamp;
      action = 'signal_detected';
      message = `Bullish crossover at ${current.close.toFixed(2)}. Waiting +${config.confirmationPercent}%`;
    }
    break;

  case 'waiting_confirmation':
    const target = state.crossoverPrice * (1 + config.confirmationPercent / 100);
    if (crossover === 'bearish') {
      state.status = 'flat';
      state.crossoverPrice = null;
      action = 'signal_cancelled';
      message = 'Bearish crossover - signal cancelled';
    } else if (currentPrice >= target) {
      state.status = 'in_position';
      state.entryPrice = currentPrice;
      action = 'buy';
      message = `Confirmed! BUY at ${currentPrice.toFixed(2)}`;
    } else {
      message = `Waiting: ${currentPrice.toFixed(2)} / ${target.toFixed(2)}`;
    }
    break;

  case 'in_position':
    if (crossover === 'bearish') {
      const pnl = ((currentPrice - state.entryPrice) / state.entryPrice * 100).toFixed(2);
      action = 'sell';
      message = `Bearish crossover - SELL at ${currentPrice.toFixed(2)}. P/L: ${pnl}%`;
      state.status = 'flat';
      state.entryPrice = null;
    }
    break;
}

return {
  json: {
    action,
    message,
    crossover,
    state: { ...state },
    current,
    currentPrice,
    symbol: config.symbol,
    timestamp: current.timestamp
  }
};
